CREATE TABLE APP.ITEM_MEDICINE (ID NUMERIC(10) NOT NULL, "NAME" VARCHAR(20) NOT NULL, DESCRIPTION VARCHAR(400), PRICE NUMERIC(10) DEFAULT 0  NOT NULL, HP NUMERIC(8) DEFAULT 0  NOT NULL, MP NUMERIC(8) DEFAULT 0  NOT NULL, INJURY NUMERIC(8) DEFAULT 0  NOT NULL, "TYPE" varchar(10) NOT NULL, EFFICACY VARCHAR(400), "LEVEL" NUMERIC(4) DEFAULT 0  NOT NULL, PRIMARY KEY (ID));
